import { NavLink, Outlet } from "react-router-dom";
import { obtainProjects } from "../data";
import { useState, useEffect, useRef } from "react";

export default function Projects() {
  let projects = obtainProjects();
  const [isSelected, setIsSelected] = useState(false);
  const [currArr, setCurrArr] = useState(projects);

  // useEffect(() => {
  //   let interval = setInterval(() => {
  //     console.log("curr array is_", currArr);
  //   }, 5000);
  //   return () => clearInterval(interval);
  // }, []);

  function filterArray(arr, id) {
    console.log(isSelected);
    setIsSelected((tf) => !tf);
    setCurrArr([arr[id]]);
  }

  function currProjectImg() {
console.log('idk what im doing')
  }

  const imgRef = useRef('project-image')
  return (
    <>
      <main className="project-list" style={{ height: "auto" ? "auto" : "100%" }}>
        <div className="project-list" style={{ padding: "1rem 0" }}>
          <nav className="nav-projects">
            {currArr.map((project, id) => (
              <NavLink
                to={`/Projects/${project.id}`}
                key={project.id}
                onClick={() =>
                  currArr.length > 1
                    ? filterArray(projects, id)
                    : setCurrArr(projects)
                }
              >
                <h4>{project.name}</h4>
                <img className={currProjectImg} ref={imgRef} src={project.img} />
                <p>Made with: {project.techs}</p>
              </NavLink>
            ))}
          </nav>
          <Outlet />
        </div>
      </main>
    </>
  );
}
